  font-family: Roboto, IRANSans, Iranian Sans, sans-serif !important;
}

.smli, .smli a.fl {
  font-size: 11px !important;
  font-style: italic !important;
  font-family: Aller, sans-serif !important;
}


.blockLink a,
#rso div.f.kv > a {
