css:
@-moz-document url-prefix("http://eel.is/c++draft/"), url-prefix("https://eel.is/c++draft/"), url-prefix("https://timsong-cpp.github.io/cppwp/") {
/***************************
*** Links and References ***
***************************/

a:link {
  color: cyan;
}

a:visited {
  color: teal;
}

a:hover {
  text-decoration: underline;
}


/**************************
*** Text and Background ***
**************************/

body {
    color: ghostwhite;
    border-left-color: ghostwhite;
    border-right-color: ghostwhite;
    background-color: black;
}

text {
    color: ghostwhite;
}

span {
    color: ghostwhite;
}



/**************************
*** Syntax Highlighting ***
**************************/

span.comment               { color: #80ff80; } /* #008000 */
span.keyword               { color: #80e1ff; } /* #00607c */
span.parenthesis           { color: #ea4e48; } /* #af1915 */
span.curlybracket          { color: #ea4e48; } /* #af1915 */
span.squarebracket         { color: #ea4e48; } /* #af1915 */
span.literal               { color: #f8bc54; } /* #9F6807 */
span.operator              { color: #ff99ff; } /* #570057 */
span.anglebracket          { color: #ff99ff; } /* #570057 */
span.preprocessordirective { color: #c4a187; } /* #6F4E37 */



/*************************
*** Figures and Graphs ***
*************************/

g {
  fill: ghostwhite;
}

g > ellipse, g > path {
  fill: none;
    stroke: ghostwhite;
}

g > polygon {
  fill: ghostwhite;
    stroke: ghostwhite;
}


/*************
*** Tables ***
*************/

table {
  border: 1px solid ghostwhite;
}

td.border {
  border-left: 1px solid ghostwhite;
}

td.cline {
  border-top: 1px solid ghostwhite;
}

tr.capsep {
    border-top: 3px solid ghostwhite;
    border-top-style: double;
}

tr.rowsep {
  border-top: 1px solid ghostwhite;
}


/*************************
*** Section Highlights ***
*************************/

:target, :target .codeblock, :target ul {
  background-color: #1a1a1a;
}
}
