css:
/* i really want this to be global */

.header-wrap {
    overflow-x: hidden;
}

.site-header .party .herobox-wrap:nth-child(n+6) {
    display: none;
}

.header-wrap {
    background: url(http://i.imgur.com/O1APgco.jpg) fixed;
    border: none;
    overflow: hidden;
}

.site-header {
    height: auto;
    width: 100%;
}

.herobox {
    background: none !important;
    outline: none;
}

.herobox .avatar-level.label {
    background: none;
    color: rgba(0,0,0,0.5);
    font-weight: 400;
}

nav.toolbar {
    background: none;
    border: none;
}

.sticky-wrapper .hero-stats {
    background: none;
    border: none;
}
