css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {
  body {
    background-color: #2f343f !important;
  }
  
  .post {
    margin: 8px;
  }
  
  .reply, .highlight {
    padding: 8px !important;
    background-color: #404552 !important;
  }
  
  .op {
    padding: 0 !important;
  }
  
  .postblock {
    background-color: #404552 !important;
  }
  
  .reply:target {
    padding: 8px !important;
  }
  
  .sideArrows {
    display: none !important;
  }
}
