css:
@-moz-document regexp("https://[^./]*\\.slack\\.com/(?!pricing)(?!security).*") {
.CodeMirror-code, .msg_inline_file_preview_toggler, .message_content_header_left {
  direction: ltr !important;
  text-align: left !important;
}
.message_body {
  font-family: IRANSans, Vazir, sans-serif !important;
  font-size: 13px;
}
ts-message:nth-child(even) {
  border-bottom: 1px dashed #ddd;   
}
#messages_container {
  max-width: 700px;
  margin: auto;
}
}
