css:
@-moz-document url-prefix("http://plnkr.co/edit/"), url-prefix("https://plnkr.co/edit/") {
    .container-fluid, .plunker-sidebar, .plunker-options {
        background-color: rgb(67, 67, 67);
        color: white;
    }
    .plunker-sidebar .plunker-filelist a {
        color: white;
    }
    .plunker-options legend {
        color: white;
    }
    .plunker-sidebar .file :hover {
        background-color: grey;
    }
}
