css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.4chan.org/?noframes"), url("https://www.4chan.org/"){
    
  body {
    font-family: "Times New Roman", "Serif"!important;
    }
    
  img:not([class=c-thumb]) {
      
	height: 0 !important;
	width: 0 !important;
	padding-left: 280px !important;
	padding-top: 239px !important;
	background: url(http://imgur.com/hCWSiG8) no-repeat !important;
    }
}

@-moz-document domain("4chan.org"), url("https://www.4chan.org/frames_navigation" ) {

  body {
    font-family: "Times New Roman", "Serif"!important;
    }
    
  h1 { 
    margin-left: 0em !important;
    background: #ffccaa !important;
    }

  h2 {
    padding: 0px !important;
    color: #000000 !important;
    margin-bottom: 0px !important;
    } 

  a {
    color: #555500 !important;
    }

  li { padding-left: 0px !important; }
  li:hover { background: #fec !important;}
  a:hover, li:hover { text-decoration:none !important; }
  }
