css:
@-moz-document domain("reader.livedoor.com"), domain("reader.livedwango.com") {

/* LDR Anonymize Content | Ver.20170502 | nukumori.org */

/* user id */
#welcome a{
    font-size: 0;
}
#welcome a:after{
    font-size: 13px;
    content: "■■■■■"
}
/* total feed count */
#total_unread_count {
    color: transparent;
}
#total_unread_count:after {
    color: #000;
	content: "未読 99999フィード  |  99999エントリ";
}
/* pin count */
#pin_count {
    color: transparent;
}
#pin_count:after {
    color: #000;
    content: "99";
}
/* feed list item */
.treeitem {
    font-size: 0 !important;
	background-image:url('http://image.reader.livedwango.com/img/icon/default.gif') !important;
}
.treeitem:before {
    font-size: 12px;
    content: " ■■■■■■■■■■■■■■■ (99)"
}
.treeitem.rs-read:before {
    font-size: 12px;
    content: " ■■■■■■■■■■■■■■■ (0)"
}
/* feed list folder */
.folderitem {
    font-size: 0;
}
.folderitem:after {
    font-size:12px;
    content: " (999)"
}
}
