css:
@-moz-document domain("vk.com") {
.audio_row .audio_row_cover_wrap, .audio_page_player .audio_page_player__cover {
	display: none;
}
.audio_row .audio_info {
	line-height: 100%;
	padding-top: 4px;
	padding-right: 8px;
}
.audio_row .audio_row_inner {
	padding: 0;
}
.audio_row .audio_acts .audio_act:first-child {
    margin: -1px 2px;
}
.audio_row .audio_acts .audio_act {
	height: 22px;
	width: 22px;
	margin: -1px 2px;
}
.audio_inline_player {
	left: 10px;
}
.audio_pl_edit_box .ape_audio_item_wrap .ape_attach {
	margin-top: 0;
}
}
