css:
@-moz-document regexp('https://app\\.codeship\\.com.*') {
    #builds_show.builds.show .rjw-content {
        max-width: none;
    }

    @media screen and (min-width: 920px) {
        #builds_show.builds.show ._step {
            padding-top: 5px;
            padding-bottom: 5px;
        }

        #builds_show.builds.show .hero {
            height: 140px;
        }

        #builds_show.builds.show ._step_time {
            top: 5px;
        }

        #builds_show.builds.show ._step_details {
            margin-top: 5px;
            margin-bottom: -5px;
        }
    }

    @media screen and (min-width: 770px) {
        #builds_show.builds.show ._step:before, #builds_show.builds.show ._step:after {
            top: 5px;
        }
    }
}
