css:
@-moz-document domain("gdax.com") {
aside.sidebar {
    display: none !important;
}

.main-content, .sub-content {
    left: 0;
}

.history-view-wrapper {
    display: block !important;
    width: 228px;
}

.user-pending {
    display: none;
}

.user-pending.fills-wrapper {
    display: none !important;
}

.chart-container .panel-item:nth-child(2) {
    display: block;
    height: 249px;
    top: 348px;
}

.ledder-view.clearfix {
    width: 228px;
}

.my-order {
    display: none !important;
}

.market-size {
    width: 57% !important;
}

.market-price {
    width: 41% !important;
}

.spread-price .market-size {
    width: 0% !important;
}

div[class^="TradeHistory__table"] > div > div > div:first-child {
    right: -17px !important;
}

div[class^="TradeHistory_market-size"] {
    display: none;
}

div[class^="OrderBookPanel_order-book-panel"] {
    width: 180px;
    overflow: hidden;
}

div[class^="TradeHistoryPanel"] {
    display: block;
    width: 210px;
}

div[class^="OrderBookPanel_content"] {
    width: 270px;
}

div[class^="OrderBookPanel_my-size"] {
    display: none;
}

.chart-container {
    left: 180px;
    right: 210px;
}

div[class^="UserPanel_user-panel"] {
    display: none;
}

div[class^="PanelHeader_tabs"], .section-nav{
    display: none !important;
}
}
