css:
@-moz-document domain("jisho.org") {
* {
    color: #EFEFEF !important;
}

body, header.row, .f-dropdown, form.search .inner, .fact, table, table tr, .discussion_thread .comment_body {
    background: black !important;
}

form.search .main, .reveal-modal, .discussion_thread .comment_body .discussion_status, .discussion_thread .comment_body .comment_status, .discussion_thread .comment_body .discussion_about {
    background: #333 !important;
}

h1.logo a {
    background-image: url('http://i.imgur.com/r83PO3P.png') !important;
}

.fact {
    border-color: #333 !important;
}

button.submit.icon {
    background: black;
}

.clear-icon {
    fill: #efefef;
}

form.search .search-form_clear-button {
    background: black;
}

.light-details_link {
    opacity: 0.75;
}

input, input[type="text"], input[type="password"], input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="month"], input[type="week"], input[type="email"], input[type="number"], input[type="search"], input[type="tel"], input[type="time"], input[type="url"], input[type="color"], textarea {
    background: black;
}

input, input[type="text"]:focus, input[type="password"]:focus, input[type="date"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="month"]:focus, input[type="week"]:focus, input[type="email"]:focus, input[type="number"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="time"]:focus, input[type="url"]:focus, input[type="color"]:focus, textarea:focus {
    background: black;
}

form.search .input_method_button:hover {
    background: black;
}

button:hover, button:focus, .button:hover, .button:focus {
    background: black;
}

button, .button {
    background: black;
}

.radical_table {
    background: black;
}
}
