css:
@-moz-document domain("mastodon.survival-machines.fr") {
  .column-header{
    border: 1px solid #393f4f;
    border-bottom: none;
  }
  
  .column > .scrollable, .column-header{
    border-left: 1px solid #393f4f;
    border-right: 1px solid #393f4f;
    background: none;
  }
  
  .column > .scrollable{
    border-bottom: 1px solid #393f4f;
    border-top: 1px solid #393f4f;
  }
  
  .column-icon, .column-back-button, .column-icon .collapsable{
    background: none;
  }
  
  .drawer__inner{
    background: none;
  }
  
  @media (min-width:1025px){
    
    .columns-area>div {
      width: 25%;
    }
    .columns-area>div>.column,
    .columns-area>div>.mastodon-column-container>.column {
      width: 100%;
    }
  }
}
