    font-family: 'Roboto Slab', IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica, Arial, sans-serif !important;
}

.hSRGPd-e1YmVc-r4nke {
    font-family: 'Roboto Condensed', IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica, Arial, sans-serif !important;
}

grammarly-btn {
	display: none !important;
}

.h1U9Be-YPqjbf {
