css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("webwereld.nl") {

/* hide top banner */
  #header {
    margin: 0 0 100px !important;
  }
  #topBanner {
    visibility: hidden;
    height: 0px !important;
  }

/* move menus into position */
  #mainMenu {
    top: 47px !important;
  }
  #subMenu {
    top: 77px !important;
  }

/*tweak columns */
  #mainColumn {
    width: 684px !important;
  }
  #rightColumn {
    width: 250px !important;
  }
  .articleListContainer div.firstColumn {
    width: 375px !important;
  }
  #videoPanel img {
    width: 250px !important;
  }  

  #category_last-item div.firstColumn, 
  #category_last-item div.secondColumn, 
  #category_last-item div.thirdColumn {
    width: 207px !important;
  }


/* fix comments */
  div.articleComment {
    background: none !important;
  }
  .articleComment h2{
    background-color: #F0F0F0 !important;
    border: 1px solid #DDDDDD !important;
  }
  .articleComment ul.articleFooter {
    background: none !important;
    border: 1px solid #DDDDDD !important;
  }
  .articleComment li.footerEnd {
    border: none !important;
  }
}
