css:
@-moz-document
  domain("novelsaga.com")
{
  body {padding-top:0;}
#sidebar {display:none}
#menu {width:97%!important;}
#container {margin:0 auto!important;}
#main {width:100%!important;margin:0 auto!important;}
.entry-byline {left:0px;}
#content div.sharedaddy, #main div.sharedaddy, div.sharedaddy {display:none;}
#header-image {max-width: 500px;height: auto;}
#menu a {line-height: 25px;}
.hentry {margin: 10px 0 10px;}
#custom-bottombar {display:none;}
#comments-title {margin: 5px 0 0 15px;}
body.custom-background {background:#1b1a1a}
#branding {margin: 0 auto;max-width:500px;}
#custom-headerbar > ul {margin: 1em 0;}
.entry-content > p {margin:1em 0;}

.entry-content {background:#212020}
.entry-byline {background:#6f6a68;border: 1px solid #4e4c4b;}

body {color:#e8e8e8;}
}
