css:
@-moz-document
  domain("zetianjitranslation.wordpress.com")
{
.secondary.widget-area {display:none;}
#page .primary {width:100%!important;padding:15px 0;}
.hentry {max-width:900px}
#jp-post-flair {display:none;}
.site-branding {margin: 10px 0 10px;}
.menu-toggle {padding:8px;}
.entry-header h1 {margin-bottom:10px;}
.entry-meta {margin-bottom:15px;}
.entry-content > p {margin:12px 0;}

html, body {background: #222223;}
.article-wrapper, .breadcrumbs-current, .comments-area {background: #2c2c2d;}
.site-footer {background:#1d1f21;}
#respond #comment-form-comment, #comment-form-share-text-padder {background:rgba(82, 79, 79, 0.85);}
#respond #comment-form-comment.active, #comment-form-share-text-padder.active {background:rgba(99, 96, 96, 0.85);}
div#actionbar {background: #444343;border: solid 1px #c8d7e1;}

body {font-size:18px;line-height:23px;font-family: "Raleway", Helvetica, Arial, sans-serif!important;}
html, button, input, select, textarea, .breadcrumbs-current {color:#d4d2d2!important;}
h1, h2, h3, h4, h5, h6 {color:#6094b7;}
#respond #comment-form-comment textarea, #comment-form-share-text-padder textarea {color: #e8e6e6!important;}
div#actionbar li.actnbr-btn > a {color: #e8e6e6}
}
