css:
@-moz-document
  domain("gravitytales.com")
{
.side-nav {display:none}
.main-content {width:100%!important;}
.btn-lg, .btn-group-lg>.btn {padding:5px 15px;}
h1, .h1, h2, .h2, h3, .h3 {margin-top: 12px;}
#contentElement {margin:0!important;}
  .container.wrap {max-width:900px;}

body {background:#1f1d1d}
.container.wrap, .comments-widget .comment-img {background:#2b2929;}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control, .form-control[enabled], fieldset[enabled] .form-control {background-color:#252525;}
.ui-68 .ui-content {border-color:#232121}
.comments-widget .comment, .widget .w-head {border-color:#232121}
.comments-widget .comment:hover {background-color:#353333}

body {color:#e8e6e6}
.comments-widget .comment-rating button {color:#484545}
.btn-link:focus, .btn-link:hover, a:not(.btn):focus, a:not(.btn):hover {color:#e0d8d8}
.header, .header a, .header a:focus, .header a:hover, .header a:visited {color:#9a9898}
#commentsList .sortorder .active, #commentsList .sortorder .active:focus, #commentsList .sortorder .active:hover, .comments-widget .comment-bar .comment-poster {color:#5d7a98}
}
