css:
@-moz-document domain(clien.net) {

.view_bottom_ad, .list_top_ad, .list_bottom_ad, .naver_ad, .list_item.hongbo, .side_ad, .view_center_ad{
	display: none !important;		
}
.post_writer .writer_board{
        width: 100%;
}

/* 읽은 글 색상 변경 */ 
.board-list a.list-subject:visited{
       color: #918c8c !important;
} 
.preview-list a:visited{
       color: #918c8c !important;
} 
/* 목록 - 2줄마다 회색배경 */
.board-list .list-row:nth-child(even) {
       background-color: #FAFAFA !important;
}
/* 댓글이 15개 이상일 경우 하이라이팅  tampermonkey 필요*/
.comment-box-30 {
    background-color: #F74D4D !important;
    color: #ffffff;
}
.comment-box-10 {
    background-color: #F3B225 !important;
    color: #ffffff;
}
/* 작성자 댓글, 내 댓글 강조 */
div.comment-row.by-me {background-color: #eeeeff;}
div.comment-row.by-author {background-color: #ffffee;}
div.comment-row.by-me:hover {background-color: #eeeeff;}
div.comment-row.by-author:hover {background-color: #ffffee;}

}
