css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/bookmarks/bookmarksPanel.xul"){
  #bookmarksPanel {
    background-color: #393F4C!important;
  }
  #search-box{
    opacity: 0.5!important;
  }
  #sidebar-search-label,
  tree{
    color: #CACACC!important;
  }
  tree{
    font-size: 14px!important;
  }
  treechildren::-moz-tree-row(selected) {
    background-color: transparent!important;
  }
  treechildren::-moz-tree-row(hover) {
    background-color: #5F6167!important;
  }
  treechildren::-moz-tree-cell-text(selected) {
    color: #CACACC!important;
  }
  @media {
    treechildren {
      --treechildren-outline:              transparent!important;
      --treechildren-2ndBottomBorderColor: transparent!important;
      --treechildren-hoverBorder:       #5F6167!important;
      --treechildren-hoverBottomBorder: #5F6167!important;
      --treechildren-hoverImage:        transparent!important;
      --treechildren-hoverSelectedImage:        transparent!important;
      --treechildren-hoverSelectedBorder:       transparent!important;
      --treechildren-hoverSelectedBottomBorder: transparent!important;
      --treechildren-currentFocusBorder:       transparent!important;
      --treechildren-currentFocusBottomBorder: transparent!important;
      --treechildren-selectedBorder:       transparent!important;
      --treechildren-selectedBottomBorder: transparent!important;
      --treechildren-selectedImage:        transparent!important;
      --treechildren-selectedFocusCurrentBorder:      transparent!important;
      --treechildren-selectedFocusCurrentBottomBorder:transparent!important;
      --treechildren-selectedFocusBorder:             transparent!important;
      --treechildren-selectedFocusBottomBorder:       transparent!important;
      --treechildren-selectedFocusImage:              transparent!important;
      --treechildren-selectedFocusCurrentImage:       transparent!important;
    }
  }
}
