css:
@-moz-document domain("moviechat.org") {
body {
	color: #B5B4BA;
	background: #1C1C1C;
}

.main {
	background: #202021;
}

.form-control {
    color: #d8d8d8;
    background-color: #2C2C2C;
}

.trending-metadata {
    color: #d8d8d8;
}

a:focus, a:hover {
    color: #00b2ea;
}

a {
    color: #0089ff;
}

.movie-overview {
    background: #272727;
}

.thread-even {
    background: #272727;
}

.board-odd {
    background: #272727;
}

.post {
    background: #272727;
}

.post-title {
    color: #d8d8d8;
}

.metadata {
    color: #9c9c9c;
}

.reply-btn {
    color: #bbbbbb;
}

.edit-btn {
    color: #bbbbbb;
}

.delete-btn {
    color: #bbbbbb;
}

.report-btn {
    color: rgba(187, 187, 187, 1);
}

.ignore-btn {
    color: rgba(187, 187, 187, 1);
}

.timestamp {
    color: #bbbbbb;
}

.pagination>li>a, .pagination>li>span {
    color: #337ab7;
    background-color: #d2d2d2;
    border: 1px solid #b3b3b3;
}

.reply {
    background: #272727;
}

.child-odd {
    background: rgb(47, 47, 47);
}

.child-even {
    background: #272727;
}

.result-even {
    background: #272727;
}
}
