css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lex.uz") {
  
  body {font-size: 12pt}
  
  
  .document_view_cont {
    max-width: 1000px;
    margin: 0 auto;
    padding-left: 20px;
    padding-right: 20px;
  }
  
  #divCont Alink {color: #1565C0;}
  #divCont Avisited {color: #512DA8;}
  
  #headermenu {
    margin: auto;
    position: fixed;
    left: 0px;
  }
  
  .ACT_FORM, .ACCEPTING_BODY, .ACT_TITLE, .ACT_TITLE_APPL, .CLAUSE_DEFAULT, .TEXT_HEADER_DEFAULT {
    font-size: 20pt;
    color: #212121;
    padding-top: 20px;
    padding-bottom: 10px;
  }
  
  div.ACT_TEXT {
    font-family: Times new roman;
    color: #212121;
    font-size: 16pt;
    padding-bottom: 2px;
  }
  
  div.CHANGES_ORIGINS {
    font-family: tahoma;
    font-style: normal;
    font-size: 12pt;
    color: #90A4AE; 
    padding-bottom: 8pt;
}
  
  div.COMMENT {
    font-family: tahoma;
    font-style: normal;
    font-size: 12pt;
    color: #90A4AE;
    padding-bottom: 10px;
  }
}
