css:
@-moz-document domain(mokum.place) {

/*
Dark Blue...: #2E4868
Light Blue..: #a4a6c5, a4a6c5
Light Gray..: #DDD;
Grayi.......: #AAA;
*/

body {
  background: #2E4868 !important;
}
.box-body {
  padding: 20px !important;
  border-radius: 5px;
}
.box-header-timeline {
  display: none;
}
.header-row {
  margin-bottom: 10px;
}
.sidebar {
  padding-left: 20px;
}
.sidebar a,
.sidebar .box-body a {
  color: #a4a6c5;
}
.sidebar .a,
.sidebar .box-footer a,
.sidebar .box-header-groups a, .sidebar .box-body .invite a {
  color: #a4a6c5;
}
.sidebar .box-body {
  padding: 0 !important;
}
.sidebar .box-body a:hover {
  color: #fff;
}
.sidebar .box-header-friends,
.sidebar .box-header-groups {
  color: white;
  border-bottom: none;
  padding: 0 !important;
}
.live_selector_pills {
  padding: 0 !important;
}
.post-body {
  margin-left: 63px;
  margin-right: 10px;
}
.attachments {
  background: #eee;
  max-height: 300px !important;
  text-align: center;
}
.attachments img {
  max-width: 100%;
  max-height: 100%;
  image-rendering: optimizeSpeed;
}
.box-footer {
  display: none;
}
h1 a {
  color: #ddd !important;
}
h1 a small {
  color: #aaa !important;
}
.sidebar .live_selector .live_selector_pills li.active .live-on, .invite-body {
  color: #DDD
}
.sidebar .live_selector .live_selector_pills li a {
  color: #AAA;
}
.sidebar .logged .logged-user .author .author_display_name {
  color: #AAA;
}
.header .title {
  margin-left: 0
}
.in-box-divider {
  display: none;
}
.search-submit {
  border: 1px solid #a4a6c5 !important;
  background: transparent !important;
  color: #a4a6c5;
}
.search-input {
  border-bottom-color: #a4a6c5 !important;
}
.alert-warning {
  display: none;
}

}
