css:
/* i really want this to be global */

body,  .wrapper {
    background: #000;
}

#addcform,
#wnmobile,
#skinstyle,
#skinstyle + a,
.header,
.column-left,
.side-bg,
.seo-text,
.share-block,
.social-likes-block,
.full-title-block {
    display: none;
}

.column-right {
    padding-left: 0;
}

.full-block {
    display: flex;
    flex-direction: column;
}

.full-block > * {
    order: 2;
}

.player-block {
    order: 1;
}

.matrix {
    margin-bottom: 10px;
}

.screens {
    margin-top: 40px;
}
