css:
@-moz-document domain("rs-online.com") {
html, body, #outerwrapper, .mainContent {
	width: 100%;
/*     max-width: 100%; */
    box-sizing: border-box;
}
.srtnLayout{
	width: calc(100% - 20px);
}
.srtnRightDiv{
	width: calc(100% - 210px);
}

.srtnRightDiv > *{
	width:100%;
}
#SRTNResultViewWidgetActionsrtnResultViewId, #SRTNResultHeaderWidgetActionsrtnHeaderId{
    float:none;
    width: 100%;
}
#viewport, #dockHeaderRight, #header_viewport{
	width:100%;
/*     overflow-x: scroll; */
    word-break: break-all;
    display: inline-block;
    box-sizing: border-box;
}

.attrColCell > div, .attrColHeader > div {
	width: 50px !important;
}



/* hide adds */
.valuePropositionImg{
	display: none;
}
}

@-moz-document regexp("https?://[^/]*rs-online\\.com/web/p.*") {
.mainContent {
	width: 1010px;
	margin: 0 auto;
	float: none;
}
}
