css:
@-moz-document url(http://192.168.113.239:8080/) {
body {
    width: 97%;
    margin: 10px 1.5%;
    background-color: #34495e;
}

#bodyWrapper {
    background-color: #ffffff;
}

.headBG {
    height: 70px;
    padding: 17px 0;
}

div.fixedWidth {
    max-width: 100%;
}

.tableCaption {
    padding: 10px 0;
}

.tableCaption.branch-header:hover,
.tableCaption.exp:hover,
.modification-row.vcs-row:hover {
    background-color:#ecf0f1;
}

#changeLogTable table.overviewTypeTable td.changeDescription {
    line-height: 1.5em;
}

.branch .branchName{
    font-size:13px;
}

table.overviewTypeTable td {
    padding: 0.4em 0;
}

.tableCaption.branch-header:hover .btn, .tableCaption.branch-header:hover .healthItemIndicator, .tableCaption.branch-header:hover .ui-datepicker-buttonpane button,
.tableCaption.exp:hover .btn, .tableCaption.exp:hover .healthItemIndicator, .tableCaption.exp:hover .ui-datepicker-buttonpane button {
    background-color: #1abc9c;
    color: #fff;
    text-shadow: none;
    border: 1px solid #16a085;
    border-bottom-color: #16a085;
    box-shadow: inset 0 1px 0 rgba(1, 1, 1, 0.1), 0 1px 2px rgba(0, 0, 0, 0.1);
}
}
