css:
@-moz-document url-prefix("https://facebook.github.io/react/") {
.documentationContent, .communityContent {
    background-color: #333;
    color: #ccc;
}

.nav-docs {
    color: #ccc;
}

.nav-docs a {
    color: #eee;
}

.highlight {
    background-color: #222;
}

.highlight:after {
    background-color: #111;
}

p code {
    background-color: #222;
    color: #ddd;
}
}
