css:
@-moz-document domain("friends.nico") {
  @media screen and (min-width: 1025px) {
    .announcements,
    .navigation-bar,
    .nico-connect-account {
      display:none;
    }
    .columns-area {
      width:100%;
      height:100%;
      flex-wrap:wrap;
      overflow:hidden;
    }
    .columns-area > div {
      width:33.33%;
      height:100%;
      order:0;
    }
    .columns-area > div.drawer {
      height:50%;
      padding-bottom:0;
    }
    .drawer > div {
      margin-bottom:5px;
    }
    .drawer, .column {
      padding:5px;
    }
    .columns-area > div:nth-child(3) {
      height:50%;
      top:-50%;
      padding:0 5px 5px 10px;
      order:2;
    }
    .columns-area > div:nth-child(3) .column-icon-clear {
      top:0px
    }
    .columns-area > div:nth-child(4) {
      height:75%;
      order:0;
    }
    .columns-area > div:nth-child(5) {
      height:25%;
      top:-25%;
      margin-left:33.33%;
      padding:0 5px 5px 5px;
      order:3;
      transition:0.4s;
    }
    .columns-area > div:nth-child(5):hover {
      top:-100%;
      padding-top:5px;
      height:100%;
      z-index:9999;
    }
    .media-gallery a {
      width:100%;
      height:100%;
      background-size:auto 100%;
      background-position:left top;
    }
  }
}
