css:
@-moz-document domain("www.agerpres.ro") {


html,
body,
#page-header,
.main-group,
.noprint,
.inner,
.slider,
.span2,
.second-group,
.span3,
.span1,
.news-ticker,
.news-item,
.location-time,
.p15 {
    background: none !important;
 	background-color: hsl(0, 0%, 30%) !important;   
    color: hsl(0, 0%, 80%) !important;
    border: 0 !important;
}

.box-ltgray,
.btn-primary,
.btn-orange,
.btn-lightblue,
.btn-secondary {
    background-color: hsl(0, 0%, 50%) !important;   
    color: hsl(0, 0%, 80%) !important;
}

a {
    color: hsl(0, 0%, 90%) !important;
    text-decoration: none !important;
}

a:hover {
    background-color: hsla(0, 0%, 0%, 0) !important;
    color: hsl(0, 0%, 70%) !important;
}

.shadow {
    box-shadow: none !important;
    border: none !important;
}


#flux_content .n_articol:nth-child(odd) {
    background-color: hsla(0, 0%, 10%, 0) !important;
    color: white;
}

.trigger_box_info a {
    color: white !important;
}

.main_detail_news .details_news h2,
.description_articol p {
    color: white !important;
}

.recent_news {
        background-color: hsla(0, 0%, 10%, 0) !important;
}



.unit_last_news:hover {
        background-color: hsla(0, 0%, 20%, 0) !important;
}


}
