css:
@-moz-document domain("www.sueddeutsche.de") {
html,
body,
.sz-header-logo,
.header-products,
.mainnav,
.sz-header,
.ie-header-wrapper,
.flyout,
.basebox,
.mediabanderole,
.mediabanderole__head,
.department-teaser__header,
.basebox,
.escapism,
#escapism-content,
.teaserlabel,
.article-paging-nav-btn,
#homepagenewsticker,
#homepagenewsticker .content,
.red {
    background: none !important;    
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

#wrapperbelt,
#wrapper,
.sz-pilot,
.authorContainer,
.authorProfileContainer,
.authorProfiles,
.moreInfo,
.breadcrumbs li h1,
.timeformat,
.article>.header strong,
.author,
.more,
.paging__bullet--desktop,
.tile {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: 0 !important;
    
}

.table_forecast1,
.interactiveeditorial {
 	display: none !important;   
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

.paging__bullet--active {
    background-color: hsl(0, 0%, 30%) !important;
	color: hsl(0, 0%, 100%) !important;   
}

.article-paging-nav-btn:hover {
    background-color: hsl(0, 0%, 40%) !important;
    color: hsl(0, 0%, 100%) !important;
}

}
