@-moz-document domain("www.liberation.fr") {
html,
body,
.main-nav,
.pined-bottom,
.campagne-opt-in {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.live-narrow,
.live-content,
.locality-search,
.home-box,
.top100-content,
.button,
.container-column,
.article-box,
.white-box,
#DIGITEKA,
.circulation-bloc,
.bloc-next-article {
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}


.home-une-content {
    background: none !important;
    background-color: hsla(0, 0%, 50%, 0.6) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}
}
