css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.tumblr.com/dashboard") {
  .l-header-container, .l-header-container--refresh, .l-content, .left_column, .right_column, .post_avatar, .post_avatar_link {
    background-color: #2f343f !important;
  }
  
  .identity {
    background: #2f343f !important;
  }
  
  .follow_list_item_blog::before, .radar_avatar::before, .right_column::after, .rapid-recs {
    background-image: none !important;
    border-right: none !important;
  }
}
