css:
@-moz-document domain(soundcloud.com) {

html{
    filter: invert(1) sepia(1) saturate(1.2) brightness(.9) contrast(1.2) 
}
.dropdownMenu,.linkMenu{
    filter:invert(1);
}
.image__lightOutline .image__full,.profileHeaderBackground,.sc-media-image img, .sc-media-extra img, .sc-media-object, .sc-media-image .sc-artwork, .sc-media-extra .sc-artwork,video,audio  {
    filter: invert(1);
}
.header__logo{
    filter:brightness(.3);
}
.profileHeaderBackground{
    filter:brightness(.8) invert(1)
}
.sc-text-verylight a{color:#999}
body{
    background: #0a0805!important;
}
*[role="banner"]{
    filter:invert(1) contrast(1.2);
}
input{
    filter: invert(1);
}
body::-webkit-scrollbar {
    width: 3px;
}
 
body::-webkit-scrollbar-track {
    background-color:#0a0805;
}
 
body::-webkit-scrollbar-thumb {
  background-color:#9B845A;
}
.m-user {
    filter: none!important;
}

}
