css:
@-moz-document domain("www.theatlantic.com") {

html,
body,
figcaption,
#action-bar,
.c-menu__section,
#main-navigation,
.c-nav__container ul,
.c-nav__item,
.c-search__container,
#site-footer,
.c-section-header__container,
.btn--sharekit__links,
#site,
.c-card,
.c-card--,
.c-hp,
.c-latest,
.c-writers__container,
.c-popular__container,
.l-hp-b-top__off-off-lede,
.l-hp-b-top__belt-stories,
.c-feature__content,
.c-feature__image,
.c-feature,
.c-feature--,
.l-hp-b-top__off-off-lede-stack,
.l-hp-b-top__bottom-stack,
.c-cover-story__content,
.l-hp-b-top__stack-stories,
.l-hp-b-top__off-lede-stories,
.c-sections {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.most-popular ul li,
.article-inner,
.pinboard li,
blockquote,
#disqus_thread,
#disqus_thread .post-content{
    background: none !important;
    background-color: hsl(0, 0%, 50%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

.c-ad {
 	display: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

.article h2 {
    color: hsl(0, 0%, 80%) !important;
}

@media (min-width: 576px) {
    .l-hp-b-top {
		background-color: hsl(0, 0%, 30%) !important;
	}
}


}
