css:
@-moz-document domain("www.wsj.com") {
html,
body,
.header,
.DJHat,
.md-strip,
.DJHat-inner,
.mega-nav,
.marketDataHeader,
.subSections,
.realtor-body,
.top-links-wrapper,
.footer-wrap,
.product-links-wrapper,
.carousel-wrap {
    background: none !important;
	background-color: hsl(0, 0%, 30%) !important;
    color: hsl(0, 0%, 80%) !important;
    border: none !important;
}

a {
	color: hsl(0, 0%, 90%) !important;
}

a:hover {
	color: hsl(0, 0%, 70%) !important;
}

.wsjSearch {
 	 background-color: hsla(0, 0%, 30%, 0.9) !important;  
}

.wsj-headline,
.carousel-headline {
    color: hsl(0, 0%, 80%) !important;
}

.wsj-headline:hover {
    color: hsl(0, 0%, 60%) !important;
}
}
