css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.lastpass.com") {

#header, #nav-header a, #nav-header a span,
div.rules, div.post, .panel, .cp-mini, ul.topiclist li,
div.forabg, div.forumbg, div.panel-wrapper,
.nav-links a, .nav-links a.first span,
.nav-links a span, .buttons div a, .buttons div a span
{
background: none !important;
}
#header, #page-header, #page-body, #footer, #phpbb
{
background-color: #111111 !important;
}
input, textarea
{
background-color: #000000 !important;
}
ul.topiclist li
{
background-color: #222222 !important;
}
blockquote
{
background-color: #333333 !important;
}
.buttons div
{
background-color: #FF0000 !important;
}
.buttons div:hover
{
background-color: #555555 !important;
}
ul.topiclist li, .post, blockquote, input, textarea
{
border: 1px #FF0000 solid !important;
}
a, input, textarea, .buttons div a
{
color: #FFFFFF !important;
}
body, .author, h3, .fields1
{
color: #CCCCCC !important;
}
}
