css:
@-moz-document regexp('https://www.geocaching.com') {

	body > nav:first-of-type {
	border-top: 0 !important;
}

nav#Navigation {
	min-width: 0 !important;
	display: inline-block !important;
	position: absolute !important;
	top: 0 !important;
	left: 0 !important;
}

body > nav:first-of-type, nav#Navigation {
	background: #2d4f15 !important;
}

body > nav:first-of-type .logo, nav#Navigation .Logo {
	color: #ffffff !important;
	fill: #ffffff !important;
}

.menu > li > a, nav#Navigation .Menu li a {
	color: #ffffff !important;
}

nav#Navigation > .container {
	display: inline-block !important;
	width: inherit !important;
	padding: 1em 0 !important;
}

nav#Navigation > .container, nav#gcNavigation > .wrapper {
	border-bottom: none !important;
}

nav#Navigation .Menu {
	display: inline-block !important;
	margin-left: 10px !important;
}

.profile-panel {
	top: -15px !important;
}

header#ctl00_siteHeader {
	position: relative !important;
}


}
