css:
@-moz-document domain(leetcode.com) {
    body,
    .question-list-table tbody tr,
    .list-group-item,
    .progress-panel-base,
    .list-item,
    .like-buttons,
    .editor-base,
    .playground-btn,
    .setting-btn,
    .reset-btn,
    .code-btn,
    .popover,
    .modal-content,
    .Select-input,
    .Select-placeholder,
    .Select--single>.Select-control .Select-value,
    .Select-arrow-zone,
    .panel-default>.panel-heading,
    .panel-body,
    .topic .vote-buttons,
    blockquote,
    .topic [component="post/parent"],
    .dropdown-menu>li>a:focus,
    .dropdown-menu>li>a:hover,
    .form-control,
    .table-striped,
    .table-bordered,
    .table-hover,
    table,
    .table,
    th,
    tr,
    td,
    .solution-base .solution-link-bar,
    .topic-body,
    .playground-mini-base .lang-btn-set-base .lang-btn-set button,
    .discuss-list-base .discuss-item,
    .discuss-main-topic-base .header-bar-base,
    .side-block,
    .post-base,
    .info-panel,
    .comment-panel,
    .lc-comment,
    .comment-base,
    .comment-panel,
    .discuss-tool-bar.default-sticky,
    .dropdown-menu,
    .btn,
    #sessionMenu,
    span.text-sm.text-gray,
    .pager .disabled>a,
    .pager .disabled>a:focus,
    .pager .disabled>a:hover,
    .pager .disabled>span,
    .pager li>a,
    #result_wa_testcase_input,
    .msg .description,
    .side-block-info,
    .problem-category .problem-category-info a:hover,
    .notifications-container-compact,
    .notifications-scroll-container,
    .xp-total,
    .rc-dialog-content,
    .Select-control,
    .Select-value-label,
    .progress-panel-base>ul li.list-group-item.points-info .info-value {
        /* backgroud; black-2; font: white-1 */
        background-color: #555 !important;
        color: #dedede !important;
        border-width: 0 !important;
        border: 0 !important;
    }

    div.question-description>div>pre,
    #discussSolution>div.panel.panel-default>div.panel-body>div>pre,
    div.topic-text>div>pre,
    code li>pr,
    code,
    div.post-panel>p>pre,
    div.comment-content>p>pre,
    div.question-description>div>div>pre,
    .lc-comment .comment-body pre {
        /* backgroud: white-1; font:  black-2*/
        background-color: #ccc !important;
        color: #555 !important;
        border: 0px !important;
    }

    div.question-description>div>pre,
    .question-content pre,
    .panel-body>div>pre,
    .panel-body>div>div>pre,
    p>code,
    li>code,
    .navbar-default,
    div.question-description>div>div>pre,
    .ace_gutter-layer,
    .ace_content {
        /* backgroud: black-1; font:  white-1*/
        background-color: #333 !important;
        color: #ecf0f1 !important;
        border: 0px !important;
    }

    button.icon-btn:hover,
    a.icon-btn:hover,
    button.icon-btn:focus,
    a.icon-btn:focus,
    button.icon-btn.focus,
    a.icon-btn.focus,
    button.tab-item.icon-btn.active,
    a.tab-item.icon-btn.active,
    button.btn.btn-default.btn-round,
    .dropdown-menu {
        /* backgroud: black-3; font : white-1*/
        background-color: #777 !important;
        color: #dedede !important;
        border: 0px !important;
    }

    /* link */
    a,
    footer.site-footer .links a {
        color: #b2d8f9 !important;
        /* light blue */
    }

    a:hover {
        color: #f5f5f5 !important;
        /* white */
    }

    /* remove border */
    .cm-s-monokai.CodeMirror,
    .side-bar,
    .question-panel,
    .modal-header,
    .modal-body,
    .modal-footer,
    .editor-base .btn-round,
    .editor-base .Select-control,
    .panel-default>.panel-heading,
    .panel-default,
    .form-control,
    .table-striped,
    .table-bordered,
    .table-hover,
    #details-summary,
    .callout.callout-info,
    .panel,
    .post-base,
    .post-panel,
    .post-info,
    .side-block,
    .editor,
    #add-to-favorite .dropdown .right-btn,
    #add-to-favorite .dropdown .left-btn,
    .storyboard {
        border-width: 0 !important;
        border: 0 !important;
    }

    /* hide some elements */
    #announcement,
    #chat-link,
    .separator-line,
    #list-card-app,
    hr.line,
    #survey,
    .close-survey,
    #desktop-side-bar>div>ul>li.list-item-tags.tags>p,
    hr,
    .SubscriptionCheckWrapper,
    a.btn.btn-default.btn-xs.manage-btn {
        display: none;
    }


    /*  Other */
    .fa-thumbs-up {
        color: #449d44 !important;
    }

    .fa-thumbs-down {
        color: #d05451 !important;
    }

    .lc-comment .comment-body .read-more .trans {
        background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0), #555) !important;
    }

    .write-comment-base .create-comment .comment-panel.reply-panel {
        background: #ccc !important;
    }
}
