css:
@-moz-document domain("uludagsozluk.com") {
body {
    background-color: #333333;
}

button.button.button-search {
    background-color: #333;
}

.navbar-inner {
    -webkit-box-shadow: 0 1px 10px rgba(0,0,0,0.1);
    -moz-box-shadow: 0 1px 10px rgba(0,0,0,0.1);
    box-shadow: 0 1px 10px rgba(0,0,0,0.1);
    background-color: #111;
    background-image: linear-gradient(to bottom,#111,#111);
    background-repeat: repeat-x;
}
.navbar-form input,.navbar-form input:hover, .navbar-form select, .navbar-form .radio, .navbar-form .checkbox {
    text-indent: 25px;
    color: #fff;
    border: 1px solid #090909;
    width: 642px;
    background-color: rgb(172,172,172);
    border-radius: 5px;
    height: 20px;
    transition: all 0.5s;
    position: relative;
}
.entryler li, .entry {
    background: #2a2a2a !important;
    color: #fefefe;
}

.kuladi, .entry a {
    color: #eee;
}

.baslik_list a, .baslik_list a:hover {
    background-color: #72a879;
    padding-left: 10px;
    padding-right: 10px;
}
.takip_eski  {
	border-radius: 20px;
}

.index-list li, .index-list li.active {
    background: #333 !important;
    color: #efefef;
    border: none !important;
}

.index-list{
    box-sizing: initial;
}

li small {
    background: #72a870 !important;
    color: #fff !important ;
}

.index-ic {
    background-color: #333 !important;
    color: #efefef !important;
    border-top: none !important;
    box-shadow: none !important;
}
.index-ic a{
    background-color: #333 !important;
    color: #efefef !important;
}

.box ul li {
    background-color: #72a875;
    color: #fff;
}
.box ul li a, li.active a{
    color: #fff;
}

li.active {
    background-color: #5c95a0 !important;
    color: #fff;
}

}
