css:
@-moz-document domain("srf.ch") {

body,
.footer-top,
.footer-bottom {
    background-color: #202020;
}
.l-main-wrapper,
.l-card,
.comments-header--top,
.comments-header,
.load-more,
.swipemod-showall,
.swipemod-container,
.swipemod-item,
.sharing-bar__container,
.webpush-registration__button {
    background-color: #2d2d2d;
}
.media-caption,
.related-items-list__content:hover,
.swipemod-swipecontainer .swipemod-item:hover,
.breadcrumb__link,
.breadcrumb__link:hover,
.webpush-registration__button:hover,
.button .button--webpush,
.button,
.button:hover,
.comments-header__button {
    background-color: #404040;
}
.button {
    transition: none;
}
.article-content,
.article-title__text,
.article-author__date,
.media-caption__description,
.media-caption__source,
.sharing-bar__summary,
.related-items-list__heading,
.article-teaser__title,
.article-author,
.comments-header__title,
.comment__user,
.comment__time,
.swipemod-item__title,
.navigation-link__text,
.footer-bottom__navigation-link,
.footer-bottom__description,
.footer-bottom__link,
.breadcrumb__link,
.webpush-register,
.webpush-registration__title,
.webpush-registration__info,
.webpush-registration__button,
.breadcrumb__link:hover,
.comment__content,
.button,
.comments-header__button,
.tab-option__link,
.module a {
    color: #BBBBBB;
}
.module {
    background-color: #2d2d2d !important;
    color: #BBBBBB;
}
.module > ol,
.module > ol li.last-child,
.module > ul,
.module > ul li h3:last-child,
.module > ul li.last-child,
.module > ul li:last-child {
    background-color: #2d2d2d;
}
.gradient-rhc,
.module.related {
    background-image: none !important;
}
.primary-nav > ul.nav > li ul.nav > li > ul,
.top-level-nav ul.nav > li > ul,
.module.comments .alert-info,
.dropdown-menu,
.gradient-rhc,
.module.related,
#primary-nav > ul > li.active,
#primary-nav > ul > li.active a {
    background-color: #2d2d2d !important;
}

}
