css:
@-moz-document url-prefix(http://localhost:8888) {
body {
    font-family: serif;
}
.rendered_html code {
    background-color: #eee;
}
.rendered_html :not(pre) > code {
    padding: 1px 1px;
}
.rendered_html pre code {
    background-color: #eee;
}
.rendered_html pre {
    border: 1px;
    background-color: #eee;
    padding: .5ex .5em;
}
}
