css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mastodones.club"), domain("mastodon.social") {
  .ui, .accounts-grid {background-color: #263238 !important;}
  .entry, .account-grid-card {background-color: #4f5b62 !important;}
  .column-back-button, .column-header, .column-icon, .drawer__header *, .navigation-bar {
    background-color: #000a12 !important;
    color: #eee !important;
  }
  .drawer__header *:hover{
    /*background-color: #263238 !important;*/
    background-color: #eee !important;
    color: #000a12 !important;
  }
  .drawer__tab > i {background-color: transparent !important;}
  .drawer__tab:hover > i {color: #000a12 !important; background-color: transparent !important;}
  .column > .scrollable, .drawer__inner, .detailed-status, .detailed-status__action-bar, .entry {background: #4f5b62 !important;}
  .status__content, .status__content p {color: #eee !important;}
  .status__relative-time {color: #000a12 !important;}
  .status__content a, span.ellipsis, .status__prepend, .icon-button {
    color: #babdbe !important;
  }
  .entry .status, .account-grid-card__header {border-bottom: 1px solid #000a12 !important;}
  .account-grid-card {border-color: #000a12 !important;}
}
