@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugzilla.com") {
  
body {background-color:#333333}
#bugzilla-body {background-color:#333333; border-color:#60666a; color:#c0c0c0}
.bz_comment {background-color:#333333; border-color:#60666a}
.bz_alias_short_desc_container {background-color:#292929; border-color:#60666a; color:#c0c0c0}
.bz_time_tracking_table th {background-color:#9d9c9c; border-color:#60666a; color:#030202}
#attachment_table th, .bz_attach_footer, .bz_time_tracking_table th {background-color:#9d9c9c; border-color:#60666a; color:#030202}
#titles {background-color:#292929; border-color:#60666a; color:#c0c0c0}
#header .links, #footer {background-color:#555555; border-color:#60666a; color:#c0c0c0}
.bz_row_odd {background-color:#6b6a6a; border-color:#60666a; color:#030202}
.bz_row_even {background-color:#9d9c9c; border-color:#60666a; color:#030202}
input.required, select.required, span.required_explanation {background-color:#1C1F22; color:#c0c0c0}
#Create .comment {color:#c0c0c0}
#message {border-color:#60666a; color:#c0c0c0}
.header_addl_info:after {content: ", DarkBugs v0.3 by Juan Ram√≠rez"; }
}
