css:
@-moz-document domain("sekurak.pl") {
body, input, textarea, select, button {
	background-color: #0e0e0e;
	color: #eaeaea;
}
header#header {
	background-color: #0e0e0e;
}
header#header nav {
	background: #232323;
}
a.text2link, header#header nav ul li a, .postTitle a, .meta a, .commentlist .comment-author a, #sidebar a {
	color: #b9b9b9;
}
header#header nav ul li a {
	border-right: 2px solid #b9b9b9;
}
.boxGreen {
	background-color: #080b02;
	border: 1px solid #26360c;
}
.boxBlue {
	background-color: #0e161c;
	border: 1px solid #1b2e41;
}
.boxRed {
	background-color: #240f06;
	border: 1px solid #3d1401;
}
.boxGrey {
	background: #222222;
	border: 1px solid #454545;
}
.widgetTitle {
	background: #232323;
}
.widget .textwidget {
	background-color: #131313;
}
.widget ul {
	background-color: #131313;
}
.widget ul li {
	border-bottom: 1px solid #222222;
}
hr {
	border-top: 1px solid #222222;
}
input, textarea, select {
	border: 1px solid #222222;
}
.entry h2 {
	border-bottom: 1px solid #242424;
}
footer#footer {
	background: #000000;
}
#content #ppc-box .ppc-box .ppc h3 {
	background: #232323;
}
#content #ppc-box .ppc-box .ppc {
	border: 1px solid #222222;
	background: #131313;
}
#content #ppc-box .ppc-box .ppc ul li a {
	color: #ccc;
}
body .wp-pagenavi a {
	background: #151515;
}
body .wp-pagenavi .current {
	background: #333333;
}
#s {
	color: #000000;
}
blockquote {
	background-color: #151515;
	border: 1px solid #242424;
}
.commentlist li {
	background: #111111;
}
.commentlist .comment-body {
	background: #111111;
	border: 1px solid #111111;
}
.commentlist .byuser .comment-body {
	background: #0e0e0e;
	border: 1px solid #222222;
}
.commentlist .comment-author, a.text2link, header#header nav ul li a, .postTitle a, .meta a, .commentlist .comment-author a, #sidebar a {
	color: #ffffff;
}
.comment-reply-link {
	background: #202020;
	color: #eaeaea;
	border: 1px solid #222222;
}
.comment-reply-link:hover {
	background: #353535;
}
a:hover {
	color: #ffffff;
}
.wp-caption {
	border: 1px solid #222222;
	background-color: #131313;
}
.sekuraktable tbody tr:nth-child(odd) {
	background-color: #151515;
}

}
