css:
@-moz-document domain("youtube.com") {

/* notifications pop-up */
ytd-multi-page-menu-renderer[menu-style="multi-page-menu-style-type-notifications"] {
	position: fixed;
	left: 0;
	width:  auto;
	height: calc(99vh - 48px);
	max-width:  none !important;
	max-height: none !important;
}

/* items */
ytd-notification-renderer {
	display: inline-block;
	width: 33.3%;
	
	padding: .5em 0 .5em 0;
	border-bottom: none;
}

#new.ytd-notification-renderer {
	margin-left:  .1em;
	margin-right: .1em;
}
.message.ytd-notification-renderer {
	margin-bottom: 0;
	font-weight: normal;
}
.thumbnail-container.ytd-notification-renderer {
	margin-left:  .2em;
}
a.yt-simple-endpoint.ytd-notification-renderer {margin-right: 1em}

ytd-menu-renderer.ytd-notification-renderer {right: 0}
ytd-menu-renderer.ytd-notification-renderer #button.ytd-menu-renderer {width: 2.5rem}

}
