css:
@-moz-document regexp(".*(service-now.com.*|.*servicenow-).*") {
    .navpage-pickers .selector {
        width: 350px;
        padding-left: 10px;
        padding-right: 10px;
    }
    @media (max-width: 1100px) {
        .navpage-pickers .selector {
            display: none;
        }
    }
    .modal-lg {
        width: 98%;
    }
    .CodeMirror-merge,
    .CodeMirror-merge .CodeMirror,
    .CodeMirror-merge-2pane {
        height: 800px;
    }
    .CodeMirror .cm-s-default {
        height: 800px;
    }
}
