css:
@-moz-document domain("weavesilk.com") {
#app-announcement{
	display: none !important;
}

#appstore-badge #badge.visible{
	display: none !important;
}

#sound-and-about .social-row .social.visible{
	display: none !important;
}

.btn.visible#btn-new .text{
	font-size:0 !important;
}

.btn.visible#btn-new .text:before{
	content: "Nouveau";
	font-size:14px !important;
}

.controls-container .row #share-wrap #btn-share.btn.visible .text{
	font-size:0 !important;
}

.controls-container .row #share-wrap #btn-share.btn.visible .text:before{
	content: "Partager";
	font-size:14px !important;
}

.row.about-row.visible p:nth-of-type(1){
	font-size:0 !important;
}

.row.about-row.visible p:nth-of-type(1):before{
	content: "Silk a été créé par Yuri Vishnevsky, avec de la musique et du son de Mat Jarvis.";
	font-size:14px !important;
}

.row.about-row.visible p:nth-of-type(2){
	font-size:0 !important;
}

.row.about-row.visible p:nth-of-type(2):before{
	content: "L'art partagé avec Silk est sous licence Creative Commons.";
	font-size:14px !important;
}
.row.about-row.visible p:nth-of-type(3){
	font-size:0 !important;
}

.row.about-row.visible p:nth-of-type(3):before{
	content: "Si vous avez apprécié Silk, faites le moi savoir : yurivish@gmail.com!";
	font-size:14px !important;
}

.controls-container .controls-row .label:nth-of-type(1){
	font-size:0 !important;
}

.controls-container .controls-row .label:nth-of-type(1):before{
	content: "Glisser la souris sur les couleurs pour les mélanger.";
	font-size:14px !important;
}

.controls-container .controls-row .label:nth-of-type(2){
	font-size:0 !important;
}

.controls-container .controls-row .label:nth-of-type(2):before{
	content: "Axes de rotation symétrique.";
	font-size:14px !important;
}

.controls-container .controls-row #toggle-mirror .description {
	font-size:0 !important;
}

.controls-container .controls-row #toggle-mirror .description:before{
	content: "Effet miroir";
	font-size:14px !important;
}

.controls-container .controls-row #toggle-spiral .description {
	font-size:0 !important;
}

.controls-container .controls-row #toggle-spiral .description:before{
	content: "Effet spiral";
	font-size:14px !important;
}


.controls-container .download-row p.instructions{
	font-size:0 !important;
}

.controls-container .download-row p.instructions:before{
	content: "Pour enregistrer, faire un clic droit sur l'aperçu et choisir Enregistrer l'image sous...";
	font-size:14px !important;
}


}
