@namespace url(http://www.w3.org/1999/xhtml);
/* Defog-SoundCloud v1 by Paint_Ninja */
@-moz-document domain("soundcloud.com") {
  body, a, p, .sc-type-light, .sc-text-light, a.sc-link-verylight, a.sc-link-verylight:hover, .sc-ministats, a.sc-ministats, a.sc-ministats:hover, .sc-ministats-small, .sc-ministats-medium, .sc-ministats-large, a.sc-link-light, a.sc-link-light:hover, .soundContext__line, .compactTrackListItem__user, .compactTrackListItem__number, .compactTrackListItem__trackTitle, .soundTitle__uploadTime, .sc-text-verylight, .sc-snippet-badge-grey, .g-form-section-title, .blockCheckbox, .chartTrack__scoreAllPlays, .featureChartColumn__previewPointsDeselected {color: #000;}
  .g-dark a, .g-dark .sc-type-light, .g-type-shrinkwrap-secondary, .g-type-shrinkwrap-secondary:visited, .g-type-shrinkwrap-large-secondary, .g-type-shrinkwrap-large-secondary:visited {color: #fff;}
  
  .sc-tag, .sc-tag:visited {background: #666; border: 1px solid #666;}
}
