css:
@-moz-document domain("jandan.net") {
.tucao-author span{
	display: block;
    margin-top: 5px;
    margin-bottom: 5px;
}
.tucao-vote {
    padding: 10px;
    color: #C8C7CC;
}
.tucao-content {
	padding: 10px 15px;
    line-height: 24px;
    font-size: 14px;
    border: 1px solid #E5E5E5;
    border-radius: 10px;
    max-height: 500px;
    overflow: auto;
}
.tucao-hot {
    border: none;
    background-color: #fffaf3;
    border-bottom: 10px;
}
  .tucao-hot  .tucao-row{
    margin: 0px 12px;
  }
.tucao-hot-title {
    font-size: 18px;
    line-height: 35px;
    font-weight: lighter;
    background-color: #ffb481;
    color: #fff;
}
.tucao-form textarea {
    width: 566px;
    height: 80px;
}
.tucao-hot .tucao-content {
    background-color: #fff0dc;
    border: 1px solid #ffe4bd;
}
.commentlist img {
    max-width: 100%;
}
.jandan-tucao {
    border: none;
    padding: 0px;
}
.jandan-tucao-close {
    margin-top: 15px;
}
.jandan-tucao-close a {
	 color: #adadad;
    padding: 6px 10px;
    border: 1px solid #d6d6d6;
    border-radius: 2px;
    background-color: #FDFDFD;
}
.jandan-tucao-close a:hover {
	color: #B1B1B1;
	border: 1px solid #E8E8E8;
	background-color: #FFFFFF;
}
.tucao-form input.tucao-nickname, .tucao-form input.tucao-email{
	height: 30px;
    margin-bottom: 5px;
}
.tucao-form {
    margin-top: -40px;
    margin-left: 10px;
}
.jandan-tucao-more {
    margin-bottom: 15px;
    margin-top: -15px;
}
.tucao-form button{
	padding: 10px 0;
	border: 1px solid #444;
}
}

@-moz-document url-prefix("http://jandan.net/qa"), url-prefix("http://jandan.net/duan") {
.commentlist .text {
	border: 1px solid #F0E4BC;
    border-radius: 10px;
    background-color: #FFF9E2;
    width: 74.5%;
    padding: 15px;
    margin: 15px 0px;
}
.commentlist li:hover{
	background-color: #F8F8F8;
}
#content ol , .commentlist{
	padding: 0px;
}
.commentlist li {
    padding: 0px 15px;
}
.tucao-content{
	background-color: #FFFFFF;
}
}
