@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stadt-bremerhaven.de") {

body {background:none !important;background-color:white !important;}
#sidebar {visibility:hidden !important;position: absolute !important;}
#mainpage, #main, #content, #header, #footer, #footerbox, .footer {width:auto !important;}
#mainpage {margin-left: 5em !important;margin-right: 5em !important;}
#mainpage {padding-right:0 !important;}
#search, #menu, #top, .entry, .postinfo, .postbot, .boxcomments {width:99% !important;}
#search, #footerclose {background:none !important;}
#searchform {width:240px !important;}
#search input {border: 1px solid grey !important;}
#footerclose {height:auto !important;}
.entry {margin-bottom: 4em !important;}
.wp-pagenavi {text-align:center !important;}

}
